from stegano import lsb

def caesar_encrypt(text, shift):
    encrypted_text = ""
    for char in text:
        if char.isalpha():
            # If character is lowercase
            if char.islower():
                shifted = ((ord(char) - ord('a') + shift) % 26) + ord('a')
            # If character is uppercase
            elif char.isupper():
                shifted = ((ord(char) - ord('A') + shift) % 26) + ord('A')
            
            encrypted_text += chr(shifted)
        else:
            encrypted_text += char

    return encrypted_text

